INSERT INTO `wp_posts` VALUES(104, 1, '2010-10-22 15:51:59', '2010-10-22 19:51:59', 'THE STORY IS SIMPLE.\r\nQuick Spikes was thought up when founder Brendan Walsh inadvertently left his golf spikes at home.  When the choice between purchasing another pair of costly golf shoes at the course or wearing his sneakers presented itself - he opted to stick to his sneakers.  It had rained earlier that day which caused his feet to slip during a majority of the shots, a frustrating occurrence for any golfer (although it served as an excellent excuse for his poor score).  This provided the motivation to develop a product that could help golfers in the event they find themselves in a similar situation.  Quick Spikes was born!  \r\n', 'Story', '', 'inherit', 'open', 'open', '', '33-revision-3', '', '', '2010-10-22 15:51:59', '2010-10-22 19:51:59', '', 33, 'http://quickspikesgolf.com/33-revision-3', 0, 'revision', '', 0);
